import React, { PureComponent } from 'react';
import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const data = [
  {
    name: 'Perk A',
    uv: 4000,
    pv: 2400,
    amt: 2400,
  },
  {
    name: 'Perk B',
    uv: 3000,
    pv: 1398,
    amt: 2210,
  },
  {
    name: 'Perk C',
    uv: 2000,
    pv: 9800,
    amt: 2290,
  },
  {
    name: 'Perk D',
    uv: 2780,
    pv: 3908,
    amt: 2000,
  },
  {
    name: 'Perk E',
    uv: 1890,
    pv: 4800,
    amt: 2181,
  },
  {
    name: 'Perk F',
    uv: 2390,
    pv: 3800,
    amt: 2500,
  },
  {
    name: 'Page G',
    uv: 3490,
    pv: 4300,
    amt: 2100,
  },
];

const BChart = (props) => {

    return (
      <ResponsiveContainer width="100%" height="100%">
        <BarChart
          width={500}
          height={300}
          data={data}
          margin={{
            top: 20,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
        </BarChart>
      </ResponsiveContainer>
    );
  }

export default BChart
