import Header from "components/Header";
import React, {useEffect, useContext} from "react";
import Grid from "@material-ui/core/Grid";
import {
  Button,
  Card,
  MenuItem,
  Select,
  TextField,
  Typography,
} from "@material-ui/core";
import AttachMoneyIcon from '@material-ui/icons/AttachMoney';
import PaymentIcon from '@material-ui/icons/Payment';
import MetricCard from "./MetricCard";
import PersonIcon from '@material-ui/icons/Person';
import PChart from "./piechart";
import BChart from "./BarChart";
import CardMembershipIcon from '@material-ui/icons/CardMembership';
import { AuthContext } from "contexts/Auth";
import firebase from "firebase/app";
import "firebase/firestore";


const GeneralDashboard = () => {

  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    const db = firebase.firestore();
    db.collection("admins")
      .doc(currentUser.uid)
      .get()
      .then((doc) => {
        const userData = doc.data();
        if (userData) {
          const businessId = userData["companyID"];
          console.log("Business ID");
          console.log(businessId);

          db.collection("users")
            .where("businessID", "==", businessId)
            .get()
            .then((querySnapshot) => {
              const emails = querySnapshot.docs.map((doc, index) => ({
                email: doc.id,
                id: index,
                group: doc.data()["group"],
              }));
              console.log("EMAILS");
              console.log(emails);
              setPeopleData(emails);
            })
            .catch((error) => {
              alert(error);
            });
        } else {
          console.log("No such document!");
        }
      })
      .catch((error) => {
        console.log("Error getting document:", error);
      });
  }, []);

  return (
    <div>
      <Header title="Dashboard" crumbs={["General dashboard"]} />
      <Grid
        container
        spacing={0}
        direction= "row"
      >

        <Grid item xs ={4}> 
          <MetricCard title={"Cost Per Employee"} number={"$12312"}> <PaymentIcon style={{display: "block", margin: "auto", width: 100, height: 70, color: "blue"}}></PaymentIcon> </MetricCard>
        </Grid>
        <Grid item xs ={4}>  
        <MetricCard title={"Number of Employees"} number={"100"}> <PersonIcon style={{display: "block", margin: "auto", width: 100, height: 70, color: "blue"}}> </PersonIcon> </MetricCard> 
        </Grid>
        <Grid item xs ={4}> 
          <MetricCard title={"Total Perks Offered"} number={"10"}> <CardMembershipIcon style={{display: "block", margin: "auto", width: 100, height: 70, color: "blue"}}> </CardMembershipIcon> </MetricCard> 
        </Grid>
        </Grid> 

        <br/> 
        <Grid
        container
        spacing={0}
        direction= "row"
      >
        <Grid item xs={4}> 
          <Card style={{ width: "95%", padding: 10, height: 400 }} elevation={4}> <div style={{width: "100%", height: "100%"}}><h1 style={{marginLeft: 10}}>Perks</h1>
          <PChart/> </div></Card>
        </Grid> 
        <Grid item xs={8}> 
        <Card style={{ width: "95%", padding: 10, height: 400 }} elevation={4}><div style={{width: "100%", height: "100%"}}> <h1 style={{marginLeft: 10}}>Perks</h1>
          <BChart/></div> </Card>
        </Grid>
      </Grid>
    </div>
  );
};

export default GeneralDashboard;
